    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        //Crear un objeto de tipo grados
        Usuario usu=new Usuario();
        //A este objeto le asignamos un valor a su atributo centrigrados
        usu.setPeso(Float.parseFloat(jTextField1.getText()));
        usu.setAltura(Float.parseFloat(jTextField2.getText()));
        
        //Creamos un objeto de la clase conversi√≥n
        IMC i=new IMC();
        i.setUsuario(usu);
        //Aplicar el modelo
        jLabel4.setText(""+i.calculaIMC());
        
        if(i.calculaIMC()<20){
            jLabel5.setText("Es peligroso, indica posibilidad de anemia");
        }
        else if(i.calculaIMC()>19 && i.calculaIMC()<26){
            jLabel5.setText("Esta en un indice ideal");
        }
        else{
            jLabel5.setText("Puede que este en sobrepeso, tiene posibilidad de enfermedades");
        }
    }//GEN-LAST:event_jButton1ActionPerformed

